import React from 'react';
import { Card } from 'semantic-ui-react'

class Question extends React.Component {

    state = {
        choice: ''
    }

    handleChoice = (e)  => {
        this.setState({
            choice: e.target.value
        })
        if(e.target.value === this.props.question.answer){
            console.log()
        }
    }

    render() {
        return (
            <Card>
                <h1>{this.props.question.text}</h1>
                <div>
                <input type='radio' value='false' name='tf' onChange= {(e) => this.handleChoice(e)}/>False
                <input type='radio' value='true' name='tf'onChange= {(e) => this.handleChoice(e)}/> True
                </div>

            </Card>
        )
    }

}

export default Question
