    render() {
        let qs = Data.questions.splice(0,4)
        console.log(qs)
        return(
        <CardGroup  itemsPerRow={6}>
            {qs.map((q, idx) => <Question key={idx} question={q} addScore={this.props.addScore}/>)}
        </CardGroup>
        )
    }

}

export default QuestionContainer
